window.addEventListener("DOMContentLoaded",()=>{let e=Array.from(document.querySelectorAll("p, h1, h2, h3, h4, li, figure")),t=new IntersectionObserver(e=>{e.forEach(e=>{e.target.classList.remove("show"),e.target.classList.toggle("show",e.isIntersecting)})},{threshold:.3});e.forEach(e=>{t.observe(e)})});